import './update-coupon.css'
import { Field, Formik, Form } from 'formik'
import history from "../../../../history";
import CompanyService from "../../../../../../service/CompanyService";
import AutenticationService from "../../../../../../service/AutenticationService";
import { values } from 'lodash';

export default function UpdateCoupon(props) {


    const handleSubmit = (values) => {
        let password = values.password;
        if (values.password === "") {
            password = props.updateCoupon.password
        }
        CompanyService.updateCompanyCoupon(
            props.updateCompany.id,
            values.category,
            values.title,
            values.description,
            values.startDate,
            values.endDate,
            values.amount,
            values.price,
            values.image

        ).then(
            (response) => {
                alert("The coupon " + response.data.title + " was updated successfully")
                history.push("/company/my-coupons")
                window.location.reload()

            },
            (error) => {
                try {
                    if (error.response.data.string) {
                        alert(error.response.data.string);
                        return;
                    }
                    if (error.response) {
                        alert("Login expired, please login again.");
                        AutenticationService.logOut();
                    }
                } catch {
                    alert("Servers are down, please try again later.");
                    AutenticationService.logOut();
                }
            }
        )
    }



    const back = () => {
        if (window.confirm("Are you sure you want to leave this page?")) {
            history.push("/company/my-coupons")
        }
    }

    const getToday = () => {

        let today = new Date();
        let dd = today.getDate();
        let mm = today.getMonth() + 1;
        let yyyy = today.getFullYear();

        if (dd < 10) {
            dd = '0' + dd
        }

        if (mm < 10) {
            mm = '0' + mm
        }

        return yyyy + '-' + mm + '-' + dd;

    }

    return (
        <div className="UpdateCoupon">
            <button type="submit" class="btn btn-primary my-1" title="Add" onClick={back}>
                ←
            </button>

            <h2>Update Coupon</h2><br />

            <Formik

                initialValues={{
                    category: props.updateCoupon.category,
                    title: props.updateCoupon.title,
                    description: props.updateCoupon.description,
                    startDate: props.updateCoupon.startDate,
                    endDate: props.updateCoupon.endDate,
                    amount: props.updateCoupon.endDate,
                    price: props.updateCoupon.price,
                    image: props.updateCoupon.image

                }}
                onSubmit={(values, { setFieldValue }) => handleSubmit(values, { setFieldValue })}

            >
                {({ isValid, dirty, values }) => (
                    <Form >
                        <div class="form-group">
                            <label class="control-label col-sm-2"> Title </label>
                            <Field class="col-sm-2"
                                type="name"
                                name="title"
                                placeholder="Enter Title"
                                required={true}
                                autocomplete={true}
                            />
                        </div>

                        <div class="form-group">

                            <label class="control-label col-sm-2" > Category </label>
                            <Field class="col-sm-2"
                                component="select"
                                type="email"
                                name="category"
                                title={values.type}
                                placeholder="Enter Category"
                                required={true}
                                autocomplete={true}
                            >
                                <option value="FOOD">FOOD</option>
                                <option value="ELECTRICITY">ELECTRICITY</option>
                                <option value="RESTAURANT">RESTAURANT</option>
                                <option value="VACATION">VACATION</option>
                                <option value="SPORT">SPORT</option>
                                <option value="AUTOMOBILE">AUTOMOBILE</option>
                                <option value="GAMING">GAMING</option>
                                <option value="ATTRACTION">ATTRACTION</option>

                            </Field>
                            <div class="form-group">
                                <label class="control-label col-sm-2" > Description </label>
                                <Field class="col-sm-2"
                                    name="description"
                                    placeholder="Enter Description"
                                    required={true}
                                    autocomplete={true}
                                />
                            </div>

                        </div>
                        <div class="form-group">
                            <label class="control-label col-sm-2"> Enter Start Date </label>
                            <Field class="col-sm-2"
                                type="date"
                                min={getToday()}
                                name="startDate"
                                placeholder="Enter your Password"
                                required={true}
                                autocomplete={true}
                            />
                        </div>

                        <div class="form-group">
                            <label class="control-label col-sm-2"> Enter End Date </label>
                            <Field class="col-sm-2"
                                type="date"
                                min={values.startDate}
                                name="endDate"
                                placeholder="Enter End Date"
                                required={true}
                                autocomplete={true}
                            />
                        </div>

                        <div class="form-group">
                            <label class="control-label col-sm-2"> Enter Amount </label>
                            <Field class="col-sm-2"
                                type="number"
                                name="amount"
                                min="0"
                                placeholder="Enter Amount"
                                required={true}
                                autocomplete={true}
                            />
                        </div>

                        <div class="form-group">
                            <label class="control-label col-sm-2"> Enter Price </label>
                            <Field class="col-sm-2"
                                type="number"
                                step=".01"
                                min="0"
                                name="price"
                                placeholder="Enter Price"
                                required={true}
                                autocomplete={true}
                            />
                        </div>

                        <div class="form-group">
                            <label class="control-label col-sm-2"> Enter Image </label>
                            <Field class="col-sm-2"
                                type="url"
                                name="image"
                                placeholder="Enter Image"
                                required={true}
                                autocomplete={true}
                            />
                        </div>


                        <button type="submit" class="btn btn-primary my-1" title="Add">
                            Add
                        </button>

                    </Form>
                )}
            </Formik >




        </div>

    )
}
